#SISTEMA COMPATIBLE DETERMINADO Ejemplo 1%planos[x,y] = meshgrid(0:0.1:3);z = (9-x-y)/2;hold onmesh(x,y,z)z = (2*x+4*y-1)/3;mesh(x,y,z)z = (3*x+6*y)/5;mesh(x,y,z)%Punto de interseccionplot3(1,2,3,'ro','markersize',7,'markerfacecolor','r');hold offview(120,30)#SISTEMA COMPATIBLE DETERMINADO Ejemplo 2x = linspace(-3,3,100);y1 = (1-2*x)/2;y2 = 2+x;plot(x,y1,x,y2);#SISTEMA COMPATIBLE DETERMINADO Ejemplo 3x1 = linspace(-4,4,100);x2a = (3-4*x1)/2;x2b = (x1-2)/2;x2c = (1-3*x1)/4;plot(x1,x2a,x1,x2b,x1,x2c);#SISTEMA COMPATIBLE INDETERMINADO Ejemplo 4%planos[x,y] = meshgrid(0:0.1:3);z = x+y-2;hold onmesh(x,y,z)z = 1-x+y;mesh(x,y,z)z = 3*x+y-5;mesh(x,y,z)hold offview(120,30)#SISTEMA INCOMPATIBLE Ejemplo 5x1 = linspace(0,3,100);x2a = 2-x1;x2b = x1-1;x2c = 3-2*x1;plot(x1,x2a,x1,x2b,x1,x2c);